mapper.select:
SELECT p.prod_no, p.prod_name, p.prod_detail, p.manufacture_day, p.price, p.image_file, p.reg_date, p.views, p.stock
FROM product p
WHERE p.prod_no = #{value}

mapper.update:
UPDATE product
prod_name = #{prodName}, prod_detail = #{prodDetail}, manufacture_day = #{manuDate}, price = #{price}, image_file = #{fileName}, stock = #{stock}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT sub.* FROM ( select p.prod_no, p.prod_name, p.prod_detail, p.manufacture_day, p.price, p.image_file, p.reg_date, p.views, p.stock, COUNT(*) OVER() as total_row, ROW_NUMBER() OVER (
order by p.prod_no
order by p.price DESC
order by p.price
order by p.views DESC
order by p.prod_name
) as r from product p
p.prod_name LIKE '%' || #{searchKeyword} || '%'
) sub WHERE sub.r >= #{startRowNum} AND sub.r <= #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT p.prod_no, p.prod_name, p.price
					FROM product p
					
						
							
								
									p.prod_no = #{searchKeyword}
								
								
									p.prod_name LIKE '%' || #{searchKeyword} || '%'
								
								
									p.price = #{searchKeyword}
								
							
						
					 ) countTable
